angular.module("app").directive("starRating",function(){return{restrict:"A",template:'<ul class="rating"> <li ng-repeat="star in stars" ng-class="star" ng-click="toggle($index)">  <i class="fa fa-star-o"><\/i> <\/li><\/ul>',scope:{ratingValue:"=",max:"=",onRatingSelected:"&"},link:function(n){var t=function(){n.stars=[];for(var t=0;t<n.max;t++)n.stars.push({filled:t<n.ratingValue})};n.toggle=function(t){n.ratingValue=t+1;n.onRatingSelected({rating:t+1})};n.$watch("ratingValue",function(n,i){i&&t()})}}});